    return self.match_coord(mobject, 1, direction)

def match_z(self, mobject: OpenGLMobject, direction: Vector3DLike = ORIGIN) -> Self:
    """Match z coord. to the x coord. of another :class:`~.OpenGLMobject`."""
    return self.match_coord(mobject, 2, direction)

def get_mobject_type_class() -> type[OpenGLMobject]:
    """Return the base class of this mobject type."""
    return OpenGLMobject

def align_data(self, mobject: OpenGLMobject) -> Self:
    # In case any data arrays get resized when aligned to shader data
    # self.refresh_shader_data()
    for mob1, mob2 in zip(self.get_family(), mobject.get_family()):
        # Separate out how points are treated so that subclasses
        # can handle that case differently if they choose
        mob1.align_points(mob2)
        for key in mob1.data.keys() & mob2.data.keys():
            if key == "points":
                continue
            arr1 = mob1.data[key]
            arr2 = mob2.data[key]
            if len(arr2) > len(arr1):
                mob1.data[key] = resize_preserving_order(arr1, len(arr2))
            elif len(arr1) > len(arr2):
                mob2.data[key] = resize_preserving_order(arr2, len(arr1))
    return self

def align_points(self, mobject: OpenGLMobject) -> Self:
    max_len = max(self.get_num_points(), mobject.get_num_points())
    for mob in (self, mobject):
        mob.resize_points(max_len, resize_func=resize_preserving_order)
    return self

def lock_matching_data(
    self, mobject1: OpenGLMobject, mobject2: OpenGLMobject
) -> Self:
    for sm, sm1, sm2 in zip(
        self.get_family(),
        mobject1.get_family(),
        mobject2.get_family(),
    ):
        keys = sm.data.keys() & sm1.data.keys() & sm2.data.keys()
        sm.lock_data(
            list(
                filter(
                    lambda key: np.all(sm1.data[key] == sm2.data[key]),
                    keys,
                ),
            ),
        )
    return self

def unlock_data(self) -> None:
    for mob in self.get_family():
        mob.locked_data_keys = set()

def fix_in_frame(self) -> Self:
    self.is_fixed_in_frame = 1.0
    return self

def unfix_from_frame(self) -> Self:
    self.is_fixed_in_frame = 0.0
    return self

def apply_depth_test(self) -> Self:
    self.depth_test = True
    return self

def deactivate_depth_test(self) -> Self:
    self.depth_test = False
    return self

def refresh_shader_wrapper_id(self) -> Self:
    self.get_shader_wrapper().refresh_id()
    return self

def read_data_to_shader(
    self,
    shader_data: _ShaderData,  # has structured data type, ex. ("point", np.float32, (3,))
    shader_data_key: str,
    data_key: str,
) -> None:
    if data_key in self.locked_data_keys:
        return
    self.check_data_alignment(shader_data, data_key)
    shader_data[shader_data_key] = self.data[data_key]

def submobjects(self, submobject_list: Iterable[OpenGLMobject]) -> None:
    self.remove(*self.submobjects)
    self.add(*submobject_list)

def throw_error_if_no_points(self) -> None:
    if not self.has_points():
        message = (
            "Cannot call OpenGLMobject.{} " + "for a OpenGLMobject with no points"
        )
        caller_name = sys._getframe(1).f_code.co_name
        raise Exception(message.format(caller_name))

def __init__(self, *mobjects: OpenGLMobject, **kwargs: Any) -> None:
    super().__init__(**kwargs)
    self.add(*mobjects)

def __call__(self, **kwargs: Any) -> Self:
    if self.cannot_pass_args:
        raise ValueError(
            "Animation arguments must be passed before accessing methods and can only be passed once",
        )

    self.anim_args = kwargs
    self.cannot_pass_args = True

    return self

def decorator(animation_method: _Decorated) -> _Decorated:
    method._override_animate = animation_method  # type: ignore[attr-defined]
    return animation_method

def init_size(
    num: int | None,
    alignments: str | None,
    sizes: Sequence[float | None] | None,
    name: str,
) -> int:
    if num is not None:
        return num
    if alignments is not None:
        return len(alignments)
    if sizes is not None:
        return len(sizes)
    raise ValueError(
        f"At least one of the following parameters: '{name}s', "
        f"'{name}_alignments' or "
        f"'{name}_{'widths' if name == 'col' else 'heights'}', "
        "must not be None"
    )

def init_alignments(
    str_alignments: str | None,
    num: int,
    mapping: dict[str, Vector3D],
    name: str,
    direction: Vector3D,
) -> Sequence[Vector3D]:
    if str_alignments is None:
        # Use cell_alignment as fallback
        return [cast(Vector3D, cell_alignment * direction)] * num
    if len(str_alignments) != num:
        raise ValueError(f"{name}_alignments has a mismatching size.")
    return [mapping[letter] for letter in str_alignments]

def reverse(maybe_list: Sequence[_T] | None) -> list[_T] | None:
    if maybe_list is not None:
        maybe_list = list(maybe_list)
        maybe_list.reverse()
        return maybe_list
    return None

def init_sizes(
    sizes: Sequence[float | None] | None,
    num: int,
    measures: Sequence[float],
    name: str,
) -> Sequence[float]:
    if sizes is None:
        sizes = [None] * num
    if len(sizes) != num:
        raise ValueError(f"{name} has a mismatching size.")
    return [
        size if (size := sizes[i]) is not None else measures[i]
        for i in range(num)
    ]

def func(points: Point3D_Array) -> Point3D_Array:
    points[:, dim] *= factor
    return points

def multi_mapping_function(points: Point3D_Array) -> Point3D_Array:
    result: Point3D_Array = np.apply_along_axis(function, 1, points)
    return result

def R3_func(point: Point3D) -> Point3D:
    x, y, z = point
    xy_complex = function(complex(x, y))
    return np.array([xy_complex.real, xy_complex.imag, z])

def get_edges(self, faces_list: list[list[int]]) -> list[tuple[int, int]]:
    """Creates list of cyclic pairwise tuples."""
    edges: list[tuple[int, int]] = []
    for face in faces_list:
        edges += zip(face, face[1:] + face[:1])
    return edges

def create_faces(
    self,
    face_coords: Point3DLike_Array,
) -> VGroup:
    """Creates VGroup of faces from a list of face coordinates."""
    face_group = VGroup()
    for face in face_coords:
        face_group.add(Polygon(*face, **self.faces_config))
    return face_group

def get_3d_vmob_gradient_start_and_end_points(
    vmob: VMobject,
) -> tuple[Point3D, Point3D]:
    return (
        get_3d_vmob_start_corner(vmob),
        get_3d_vmob_end_corner(vmob),
    )

def get_3d_vmob_start_corner(vmob: VMobject) -> Point3D:
    if vmob.get_num_points() == 0:
        return np.array(ORIGIN)
    return vmob.points[get_3d_vmob_start_corner_index(vmob)]

def get_3d_vmob_end_corner(vmob: VMobject) -> Point3D:
    if vmob.get_num_points() == 0:
        return np.array(ORIGIN)
    return vmob.points[get_3d_vmob_end_corner_index(vmob)]

def get_3d_vmob_unit_normal(vmob: VMobject, point_index: int) -> Vector3D:
    n_points = vmob.get_num_points()
    if len(vmob.get_anchors()) <= 2:
        return np.array(UP)
    i = point_index
    im3 = i - 3 if i > 2 else (n_points - 4)
    ip3 = i + 3 if i < (n_points - 3) else 3
    unit_normal = get_unit_normal(
        vmob.points[ip3] - vmob.points[i],
        vmob.points[im3] - vmob.points[i],
    )
    if np.linalg.norm(unit_normal) == 0:
        return np.array(UP)
    return unit_normal

class ThreeDVMobject(VMobject, metaclass=ConvertToOpenGL):
    def __init__(self, shade_in_3d: bool = True, **kwargs):
        super().__init__(shade_in_3d=shade_in_3d, **kwargs)

def _get_u_values_and_v_values(self) -> tuple[np.ndarray, np.ndarray]:
    res = tuplify(self.resolution)
    if len(res) == 1:
        u_res = v_res = res[0]
    else:
        u_res, v_res = res

    u_values = np.linspace(*self.u_range, u_res + 1)
    v_values = np.linspace(*self.v_range, v_res + 1)

    return u_values, v_values

def func(self, u: float, v: float) -> np.ndarray:
    """The z values defining the :class:`Sphere` being plotted.

    Returns
    -------
    :class:`numpy.array`
        The z values defining the :class:`Sphere`.
    """
    return self.radius * np.array(
        [np.cos(u) * np.sin(v), np.sin(u) * np.sin(v), -np.cos(v)],
    )

def __init__(
    self, dimensions: tuple[float, float, float] | np.ndarray = [3, 2, 1], **kwargs
) -> None:
    self.dimensions = dimensions
    super().__init__(**kwargs)

def generate_points(self) -> None:
    """Creates the sides of the :class:`Prism`."""
    super().generate_points()
    for dim, value in enumerate(self.dimensions):
        self.rescale_to_fit(value, dim, stretch=True)

def pointify(
    self,
    mob_or_point: Mobject | Point3DLike,
    direction: Vector3DLike | None = None,
) -> Point3D:
    """Gets a point representing the center of the :class:`Mobjects <.Mobject>`.

    Parameters
    ----------
    mob_or_point
        :class:`Mobjects <.Mobject>` or point whose center should be returned.
    direction
        If an edge of a :class:`Mobjects <.Mobject>` should be returned, the direction of the edge.

    Returns
    -------
    :class:`numpy.array`
        Center of the :class:`Mobjects <.Mobject>` or point, or edge if direction is given.
    """
    if isinstance(mob_or_point, (Mobject, OpenGLMobject)):
        mob = mob_or_point
        if direction is None:
            return mob.get_center()
        else:
            return mob.get_boundary_point(direction)
    return np.array(mob_or_point)

def add_title(
    self, title: str = "Sample space", buff: float = MED_SMALL_BUFF
) -> None:
    # TODO, should this really exist in SampleSpaceScene
    title_mob = Tex(title)
    if title_mob.width > self.width:
        title_mob.width = self.width
    title_mob.next_to(self, UP, buff=buff)
    self.title = title_mob
    self.add(title_mob)

def get_horizontal_division(
