    matrix = np.array(matrix)
    if matrix.shape == (2, 2):
        new_matrix = np.identity(3)
        new_matrix[:2, :2] = matrix
        matrix = new_matrix
    elif matrix.shape != (3, 3):
        raise ValueError("Matrix has bad dimensions")
    return matrix

