def func(t):
    if t == 1:
        ChangeSpeed.is_changing_dt = False
    new_t = piecewise(
        self.rate_func(t),
        [condition(self.rate_func(t)) for condition in self.conditions],
        self.functions,
    )
    if self.affects_speed_updaters:
        ChangeSpeed.dt = (new_t - self.t) * self.anim.run_time
        self.t = new_t
    return new_t

